mapper.sql:
WHERE 1=1

	AND C.CART_ID = #{cartId}


	AND C.MBR_ID = #{mbrId}


	AND C.PROD_ID = #{prodId}


	AND C.PROD_QTY = #{prodQty}


	AND C.PROD_AMT = #{prodAmt}


	AND C.OPT_TOT_AMT = #{optTotAmt}


	AND C.CART_DTM = STR_TO_DATE(#{cartDtm}, '%Y%m%d %h%i%s')


	AND C.PROD_PATH_CD = #{prodPathCd}


	AND C.USE_YN = #{useYn}

mapper.select:
/* cartMapper.getCartListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_CART C

/* cartMapper.getCartList */
SELECT C.CART_ID ,C.MBR_ID ,C.PROD_ID ,C.PROD_QTY ,C.PROD_AMT ,C.OPT_TOT_AMT ,CART_DTM ,C.PROD_PATH_CD ,C.USE_YN ,C.REG_USER_ID ,REG_DTM ,C.UPD_USER_ID ,UPD_DTM FROM CW_CART C
ORDER BY C.CART_ID

mapper.insert:
/* cartMapper.insertCart */
	INSERT INTO CW_CART (
		CART_ID
		,MBR_ID
		,PROD_ID
		,PROD_QTY
		,PROD_AMT
		,OPT_TOT_AMT
		,CART_DTM
		,PROD_PATH_CD
		,USE_YN
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{cartId}
		,#{mbrId}
		,#{prodId}
		,#{prodQty}
		,#{prodAmt}
		,#{optTotAmt}
		,STR_TO_DATE(#{cartDtm}, '%Y%m%d %h%i%s')
		,#{prodPathCd}
		,#{useYn}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* cartMapper.deleteCart */
DELETE FROM CW_CART
WHERE
	CART_ID = #{cartId}
