mapper.sql:
WHERE 1=1

	AND C.COMP_ID = #{compId}


	AND C.COMP_NM = #{compNm}


	AND C.CNTRY_CD = #{cntryCd}


	AND C.CP_DIV_CD = #{cpDivCd}


	AND C.BIZ_REG_NO = #{bizRegNo}


	AND C.REP_NM = #{repNm}


	AND C.BIZ_TYP = #{bizTyp}


	AND C.BIZ_ITEM = #{bizItem}


	AND C.ZIP_CD = #{zipCd}


	AND C.ADDR = #{addr}


	AND C.DTL_ADDR = #{dtlAddr}


	AND C.DTL_ADDR2 = #{dtlAddr2}


	AND C.PIC_NM = #{picNm}


	AND C.PIC_PHONE_NO = #{picPhoneNo}


	AND C.HQ_YN = #{hqYn}


	AND C.STORE_YN = #{storeYn}


	AND C.AF_CP_YN = #{afCpYn}


	AND C.FRCHSE_YN = #{frchseYn}


	AND C.CUST_CP_YN = #{custCpYn}


	AND C.ORG_YN = #{orgYn}

mapper.select:
/* comapnyMapper.getComapnyListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_COMAPNY C

/* comapnyMapper.getComapnyList */
SELECT C.COMP_ID ,C.COMP_NM ,C.CNTRY_CD ,C.CP_DIV_CD ,C.BIZ_REG_NO ,C.REP_NM ,C.BIZ_TYP ,C.BIZ_ITEM ,C.ZIP_CD ,C.ADDR ,C.DTL_ADDR ,C.DTL_ADDR2 ,C.PIC_NM ,C.PIC_PHONE_NO ,C.HQ_YN ,C.STORE_YN ,C.AF_CP_YN ,C.FRCHSE_YN ,C.CUST_CP_YN ,C.ORG_YN ,C.REG_USER_ID ,REG_DTM ,C.UPD_USER_ID ,UPD_DTM FROM CW_COMAPNY C
ORDER BY C.COMP_ID

mapper.insert:
/* comapnyMapper.insertComapny */
	INSERT INTO CW_COMAPNY (
		COMP_ID
		,COMP_NM
		,CNTRY_CD
		,CP_DIV_CD
		,BIZ_REG_NO
		,REP_NM
		,BIZ_TYP
		,BIZ_ITEM
		,ZIP_CD
		,ADDR
		,DTL_ADDR
		,DTL_ADDR2
		,PIC_NM
		,PIC_PHONE_NO
		,HQ_YN
		,STORE_YN
		,AF_CP_YN
		,FRCHSE_YN
		,CUST_CP_YN
		,ORG_YN
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{compId}
		,#{compNm}
		,#{cntryCd}
		,#{cpDivCd}
		,#{bizRegNo}
		,#{repNm}
		,#{bizTyp}
		,#{bizItem}
		,#{zipCd}
		,#{addr}
		,#{dtlAddr}
		,#{dtlAddr2}
		,#{picNm}
		,#{picPhoneNo}
		,#{hqYn}
		,#{storeYn}
		,#{afCpYn}
		,#{frchseYn}
		,#{custCpYn}
		,#{orgYn}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* comapnyMapper.deleteComapny */
DELETE FROM CW_COMAPNY
WHERE
	COMP_ID = #{compId}
