mapper.sql:
WHERE 1=1

	AND CP.COMP_PROD_ID = #{compProdId}


	AND CP.COMP_ID = #{compId}


	AND CP.PROD_ID = #{prodId}


	AND CP.PROD_TYP_CD = #{prodTypCd}


	AND CP.APPL_FR_YMD = #{applFrYmd}


	AND CP.APPL_TO_YMD = #{applToYmd}


	AND CP.AD_FEE = #{adFee}


	AND CP.USE_YN = #{useYn}

mapper.select:
/* compProdMapper.getCompProdListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_COMP_PROD CP

/* compProdMapper.getCompProdList */
SELECT CP.COMP_PROD_ID ,CP.COMP_ID ,CP.PROD_ID ,CP.PROD_TYP_CD ,CP.APPL_FR_YMD ,CP.APPL_TO_YMD ,CP.AD_FEE ,CP.USE_YN ,CP.REG_USER_ID ,REG_DTM ,CP.UPD_USER_ID ,UPD_DTM FROM CW_COMP_PROD CP
ORDER BY CP.COMP_PROD_ID

mapper.insert:
/* compProdMapper.insertCompProd */
	INSERT INTO CW_COMP_PROD (
		COMP_PROD_ID
		,COMP_ID
		,PROD_ID
		,PROD_TYP_CD
		,APPL_FR_YMD
		,APPL_TO_YMD
		,AD_FEE
		,USE_YN
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{compProdId}
		,#{compId}
		,#{prodId}
		,#{prodTypCd}
		,#{applFrYmd}
		,#{applToYmd}
		,#{adFee}
		,#{useYn}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* compProdMapper.deleteCompProd */
DELETE FROM CW_COMP_PROD
WHERE
	COMP_PROD_ID = #{compProdId}
