mapper.sql:
WHERE 1=1

	AND CI.CNTRY_CD = #{cntryCd}


	AND CI.CNTRY_ABBR_CD = #{cntryAbbrCd}


	AND CI.CNTRY_NUM_CD = #{cntryNumCd}


	AND CI.CNTRY_NM = #{cntryNm}


	AND CI.CNTRY_ABBR_NM = #{cntryAbbrNm}


	AND CI.CNTRY_NM_LNG = #{cntryNmLng}


	AND CI.CNTRY_ABBR_NM_LNG = #{cntryAbbrNmLng}


	AND CI.STD_TIME = #{stdTime}

mapper.select:
/* countryInfoMapper.getCountryInfoListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_COUNTRY_INFO CI

/* countryInfoMapper.getCountryInfoList */
SELECT CI.CNTRY_CD ,CI.CNTRY_ABBR_CD ,CI.CNTRY_NUM_CD ,CI.CNTRY_NM ,CI.CNTRY_ABBR_NM ,CI.CNTRY_NM_LNG ,CI.CNTRY_ABBR_NM_LNG ,CI.STD_TIME ,CI.REG_USER_ID ,REG_DTM ,CI.UPD_USER_ID ,UPD_DTM FROM CW_COUNTRY_INFO CI
ORDER BY CI.CNTRY_CD

mapper.insert:
/* countryInfoMapper.insertCountryInfo */
	INSERT INTO CW_COUNTRY_INFO (
		CNTRY_CD
		,CNTRY_ABBR_CD
		,CNTRY_NUM_CD
		,CNTRY_NM
		,CNTRY_ABBR_NM
		,CNTRY_NM_LNG
		,CNTRY_ABBR_NM_LNG
		,STD_TIME
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{cntryCd}
		,#{cntryAbbrCd}
		,#{cntryNumCd}
		,#{cntryNm}
		,#{cntryAbbrNm}
		,#{cntryNmLng}
		,#{cntryAbbrNmLng}
		,#{stdTime}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* countryInfoMapper.deleteCountryInfo */
DELETE FROM CW_COUNTRY_INFO
WHERE
	CNTRY_CD = #{cntryCd}
