mapper.sql:
WHERE 1=1

	AND M.MENU_ID = #{menuId}


	AND M.MENU_NM = #{menuNm}


	AND M.LINK = #{link}


	AND M.MENU_DESC = #{menuDesc}


	AND M.MENU_IMG = #{menuImg}


	AND M.PRNT_MENU_ID = #{prntMenuId}


	AND M.MENU_LVL = #{menuLvl}


	AND M.SORT_SEQ = #{sortSeq}


	AND M.DISPLAY_YN = #{displayYn}


	AND M.USE_YN = #{useYn}

mapper.select:
/* menuMapper.getMenuListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_MENU M

/* menuMapper.getMenuList */
SELECT M.MENU_ID ,M.MENU_NM ,M.LINK ,M.MENU_DESC ,M.MENU_IMG ,M.PRNT_MENU_ID ,M.MENU_LVL ,M.SORT_SEQ ,M.DISPLAY_YN ,M.USE_YN ,M.REG_USER_ID ,REG_DTM ,M.UPD_USER_ID ,UPD_DTM FROM CW_MENU M
ORDER BY M.MENU_ID

mapper.insert:
/* menuMapper.insertMenu */
	INSERT INTO CW_MENU (
		MENU_ID
		,MENU_NM
		,LINK
		,MENU_DESC
		,MENU_IMG
		,PRNT_MENU_ID
		,MENU_LVL
		,SORT_SEQ
		,DISPLAY_YN
		,USE_YN
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{menuId}
		,#{menuNm}
		,#{link}
		,#{menuDesc}
		,#{menuImg}
		,#{prntMenuId}
		,#{menuLvl}
		,#{sortSeq}
		,#{displayYn}
		,#{useYn}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* menuMapper.deleteMenu */
DELETE FROM CW_MENU
WHERE
	MENU_ID = #{menuId}
