mapper.sql:
WHERE 1=1

	AND OD.ORD_DTL_ID = #{ordDtlId}


	AND OD.ORD_ID = #{ordId}


	AND OD.PROD_ID = #{prodId}


	AND OD.CART_ID = #{cartId}


	AND OD.ORD_PROD_QTY = #{ordProdQty}


	AND OD.ORD_PROD_AMT = #{ordProdAmt}


	AND OD.ORD_OPT_AMT = #{ordOptAmt}


	AND OD.OPT_DTL_CONT = #{optDtlCont}


	AND OD.CART_DTM = STR_TO_DATE(#{cartDtm}, '%Y%m%d %h%i%s')


	AND OD.PROD_PATH_CD = #{prodPathCd}

mapper.select:
/* orderDtlMapper.getOrderDtlListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_ORDER_DTL OD

/* orderDtlMapper.getOrderDtlList */
SELECT OD.ORD_DTL_ID ,OD.ORD_ID ,OD.PROD_ID ,OD.CART_ID ,OD.ORD_PROD_QTY ,OD.ORD_PROD_AMT ,OD.ORD_OPT_AMT ,OD.OPT_DTL_CONT ,CART_DTM ,OD.PROD_PATH_CD ,OD.REG_USER_ID ,REG_DTM ,OD.UPD_USER_ID ,UPD_DTM FROM CW_ORDER_DTL OD
ORDER BY OD.ORD_DTL_ID

mapper.insert:
/* orderDtlMapper.insertOrderDtl */
	INSERT INTO CW_ORDER_DTL (
		ORD_DTL_ID
		,ORD_ID
		,PROD_ID
		,CART_ID
		,ORD_PROD_QTY
		,ORD_PROD_AMT
		,ORD_OPT_AMT
		,OPT_DTL_CONT
		,CART_DTM
		,PROD_PATH_CD
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{ordDtlId}
		,#{ordId}
		,#{prodId}
		,#{cartId}
		,#{ordProdQty}
		,#{ordProdAmt}
		,#{ordOptAmt}
		,#{optDtlCont}
		,STR_TO_DATE(#{cartDtm}, '%Y%m%d %h%i%s')
		,#{prodPathCd}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* orderDtlMapper.deleteOrderDtl */
DELETE FROM CW_ORDER_DTL
WHERE
	ORD_DTL_ID = #{ordDtlId}
