mapper.sql:
WHERE 1=1

	AND P.PROD_ID = #{prodId}


	AND P.PROD_NM = #{prodNm}


	AND P.PRNT_PROD_ID = #{prntProdId}


	AND P.COMP_ID = #{compId}


	AND P.PROD_SKIN = #{prodSkin}


	AND P.PROD_MOBI_SKIN = #{prodMobiSkin}


	AND P.PROD_MFR = #{prodMfr}


	AND P.PROD_ORIGIN = #{prodOrigin}


	AND P.PROD_BRAND = #{prodBrand}


	AND P.PROD_MODEL = #{prodModel}


	AND P.PROD_AMT = #{prodAmt}


	AND P.PROD_INV_QTY = #{prodInvQty}


	AND P.PROD_USE_QTY = #{prodUseQty}


	AND P.PROD_IN_QTY = #{prodInQty}


	AND P.SOLD_OUT_YN = #{soldOutYn}


	AND P.POINT_ID = #{pointId}


	AND P.CPN_ID = #{cpnId}


	AND P.PROD_WEIGHT = #{prodWeight}


	AND P.PROD_INCL_QTY = #{prodInclQty}


	AND P.DELIV_FEE_TYP_CD = #{delivFeeTypCd}


	AND P.BASE_DELIV_FEE = #{baseDelivFee}


	AND P.DELIV_FREE_MIN_AMT = #{delivFreeMinAmt}


	AND P.DELIV_BASE_WEIGHT = #{delivBaseWeight}


	AND P.DELIV_BASE_QTY = #{delivBaseQty}


	AND P.PROD_VIEWS = #{prodViews}


	AND P.PROD_REG_DTM = STR_TO_DATE(#{prodRegDtm}, '%Y%m%d %h%i%s')


	AND P.PROD_UPD_DTM = STR_TO_DATE(#{prodUpdDtm}, '%Y%m%d %h%i%s')


	AND P.REG_IP = #{regIp}


	AND P.SORT_SEQ = #{sortSeq}


	AND P.SALES_QTY = #{salesQty}


	AND P.INC_SMS_NOTIF_YN = #{incSmsNotifYn}


	AND P.REVIEW_NUM = #{reviewNum}


	AND P.REVIEW_RTG_AVG = #{reviewRtgAvg}


	AND P.USE_YN = #{useYn}

mapper.select:
/* productsMapper.getProductsListCount */
	SELECT COUNT(1) AS CNT
	FROM CW_PRODUCTS P

/* productsMapper.getProductsList */
SELECT P.PROD_ID ,P.PROD_NM ,P.PRNT_PROD_ID ,P.COMP_ID ,P.PROD_SKIN ,P.PROD_MOBI_SKIN ,P.PROD_MFR ,P.PROD_ORIGIN ,P.PROD_BRAND ,P.PROD_MODEL ,P.PROD_AMT ,P.PROD_INV_QTY ,P.PROD_USE_QTY ,P.PROD_IN_QTY ,P.SOLD_OUT_YN ,P.POINT_ID ,P.CPN_ID ,P.PROD_WEIGHT ,P.PROD_INCL_QTY ,P.DELIV_FEE_TYP_CD ,P.BASE_DELIV_FEE ,P.DELIV_FREE_MIN_AMT ,P.DELIV_BASE_WEIGHT ,P.DELIV_BASE_QTY ,P.PROD_VIEWS ,PROD_REG_DTM ,PROD_UPD_DTM ,P.REG_IP ,P.SORT_SEQ ,P.SALES_QTY ,P.INC_SMS_NOTIF_YN ,P.REVIEW_NUM ,P.REVIEW_RTG_AVG ,P.USE_YN ,P.REG_USER_ID ,REG_DTM ,P.UPD_USER_ID ,UPD_DTM FROM CW_PRODUCTS P
ORDER BY P.PROD_ID

mapper.insert:
/* productsMapper.insertProducts */
	INSERT INTO CW_PRODUCTS (
		PROD_ID
		,PROD_NM
		,PRNT_PROD_ID
		,COMP_ID
		,PROD_SKIN
		,PROD_MOBI_SKIN
		,PROD_MFR
		,PROD_ORIGIN
		,PROD_BRAND
		,PROD_MODEL
		,PROD_AMT
		,PROD_INV_QTY
		,PROD_USE_QTY
		,PROD_IN_QTY
		,SOLD_OUT_YN
		,POINT_ID
		,CPN_ID
		,PROD_WEIGHT
		,PROD_INCL_QTY
		,DELIV_FEE_TYP_CD
		,BASE_DELIV_FEE
		,DELIV_FREE_MIN_AMT
		,DELIV_BASE_WEIGHT
		,DELIV_BASE_QTY
		,PROD_VIEWS
		,PROD_REG_DTM
		,PROD_UPD_DTM
		,REG_IP
		,SORT_SEQ
		,SALES_QTY
		,INC_SMS_NOTIF_YN
		,REVIEW_NUM
		,REVIEW_RTG_AVG
		,USE_YN
		,REG_USER_ID
		,REG_DTM
		,UPD_USER_ID
		,UPD_DTM
	) VALUES (
		#{prodId}
		,#{prodNm}
		,#{prntProdId}
		,#{compId}
		,#{prodSkin}
		,#{prodMobiSkin}
		,#{prodMfr}
		,#{prodOrigin}
		,#{prodBrand}
		,#{prodModel}
		,#{prodAmt}
		,#{prodInvQty}
		,#{prodUseQty}
		,#{prodInQty}
		,#{soldOutYn}
		,#{pointId}
		,#{cpnId}
		,#{prodWeight}
		,#{prodInclQty}
		,#{delivFeeTypCd}
		,#{baseDelivFee}
		,#{delivFreeMinAmt}
		,#{delivBaseWeight}
		,#{delivBaseQty}
		,#{prodViews}
		,STR_TO_DATE(#{prodRegDtm}, '%Y%m%d %h%i%s')
		,STR_TO_DATE(#{prodUpdDtm}, '%Y%m%d %h%i%s')
		,#{regIp}
		,#{sortSeq}
		,#{salesQty}
		,#{incSmsNotifYn}
		,#{reviewNum}
		,#{reviewRtgAvg}
		,#{useYn}
		,#{regUserId}
		,NOW()
		,#{updUserId}
		,NOW()
	)

mapper.delete:
/* productsMapper.deleteProducts */
DELETE FROM CW_PRODUCTS
WHERE
	PROD_ID = #{prodId}
